import { useState, useEffect } from "react";

const UseEffectCleanUp = () => {

    const [size, setSize] = useState(window.innerWidth);

    useEffect(() => {
        window.addEventListener()
    })

    return (
        <>
            <h2>Screen Size</h2>
            <h3>{size} Px</h3>
        </>
    );
}

export default UseEffectCleanUp;
